mapper.select:
 
			SELECT sub.subject_code as subject_code, sub.subject_name as subject_name, r.score as score, sub.credit as credit, sub.category as category, sub.professor_name as professor_name
			FROM students stu, registers r, subjects sub
			WHERE stu.student_id = r.student_id AND r.subject_code = sub.subject_code AND r.year= #{regYear} AND r.semester= #{regSemester} AND stu.student_id= #{studentId}
